import React, { useState } from "react";
import MorseToAnimation from "../MorseToAnimation";


export default function MorseToSound(props) {
  var globalAudioContext = new window.AudioContext();

  let myVar;
   let time = 0;

   let morseString = props.transcript;


    const [color,setColor] = useState('')
    const [name, setName ] = useState('type')

    const lightBulb = {
        width: "200px",
        height: "200px",
        borderRadius: "50%",
        backgroundColor: color,
        };


    function setTheTimeOutGreen(){
        time = time + 20;
        console.log('green')
        myVar = setTimeout( setGreen, time)

        time = time + 80;
        myVar = setTimeout( setNull, time)



    }

    function setTheTimeOutPurple(){
        time = time + 20;
        console.log('purple')
        myVar = setTimeout( setPurple, time)

        time = time + 280;
        myVar = setTimeout( setNull, time)


    }

    function setTheTimeOutRed(){
        time = time + 20;
        console.log('red')
        myVar = setTimeout( setRed, time)

        time = time + 680;
        myVar = setTimeout( setNull, time)
    }

    function setGreen(){
        setColor('green')
        setName('kort')
    }

    function setPurple(){
        setColor('purple')
        setName('lang')
    }

    function setRed(){
        setColor('red')
        setName('einde')
    }

    function setNull(){
        setColor('white')
    }



  let morseStringCharacters = []
    morseString.map(function (c) {

      for (let i = 0; i < c.length; i++) {

       morseStringCharacters.push(c[i])
      }

  });

  function morsecode(text, unit, freq) {
    // defaults
    unit = unit ? unit : 0.1;
    freq = freq ? freq : 50;
    var cont = globalAudioContext;
    var time = cont.currentTime;

    // morsecode
    var conversionTable = {
      a: "._",
      b: "_...",
      c: "_._.",
      d: "_..",
      e: ".",
      f: ".._.",
      g: "__.",
      h: "....",
      i: "..",
      j: ".___",
      k: "_._",
      l: "._..",
      m: "__",
      n: "_.",
      o: "___",
      p: ".__.",
      q: "__._",
      r: "._.",
      s: "...",
      t: "_",
      u: ".._",
      v: "..._",
      w: ".__",
      x: "_.._",
      y: "_.__",
      z: "__..",
      0: "_____",
      1: ".____",
      2: "..___",
      3: "...__",
      4: "...._",
      5: ".....",
      6: "_....",
      7: "__...",
      8: "___..",
      9: "____.",
    };


    function convertToMorse(transcript) {
      let morseString = [];
      for (var i = 0; i <= transcript.length; i++) {
      let toConvertChar = transcript.substr(i, 1).toLowerCase();

      let convertedChar = conversionTable[toConvertChar];
      if (convertedChar === undefined) {
      convertedChar = " ";
      }

      morseString.push(convertedChar);
      // recognised character
      }

      return morseString;
      } 


    // generate time for code
    function maketime(data) {
      for (var i = 0; i <= data.length; i++) {
        var timedata = data.substr(i, 1);
        timedata = timedata === "." ? 1 : timedata === "_" ? 3 : 0;

        timedata *= unit;
        if (timedata > 0) {
          maketone(timedata);

          time += timedata;
          // tone gap
          time += unit * 1;
        }
      }
      // char gap
      time += unit * 2;
    }

    // generate tone for time
    function maketone(data) {
      var start = time;
      var stop = time + data;

      // filter: envelope the tone slightly
      gain.gain.linearRampToValueAtTime(0, start);
      gain.gain.linearRampToValueAtTime(1, start + unit / 8);
      gain.gain.linearRampToValueAtTime(1, stop - unit / 16);
      gain.gain.linearRampToValueAtTime(0, stop);
      // toggle achtergrond rood
    }

    // create: oscillator, gain, destination
    var osci = cont.createOscillator();
    osci.frequency.value = freq;
    var gain = cont.createGain();
    gain.gain.value = 0;
    var dest = cont.destination;
    // connect: oscillator -> gain -> destination
    osci.connect(gain);
    gain.connect(dest);
    // start oscillator
    osci.start(time);

    // begin encoding: text -> code -> time -> tone
    convertToMorse(text);

    // return web audio context for reuse / control
    return cont;
  }

  return (
    <>
      <div>Hello from Morse</div>

      <button
        onClick={(e) => {
          morsecode("sos");
        }}
      >
        SOS Test
      </button>
      <button
        onClick={(e) => {
          props.startListen();
        }}
      >
        Record
      </button>
      <button
        onClick={(e) => {
          props.abortListen();
        }}
      >
        Stop Record
      </button>
      <button
        onClick={(e) => {
          morsecode(props.transcript);
          props.animationStart();
        }}
      >
        Play Morse Code Sound
      </button>
      <button onClick={(e)=>{props.animationStart()}}>test</button>


    </>
  );
}
