        myVar = setTimeout( setColor('purple'), 1000)
    }

    function setTheTimeOutRed(){
        myVar = setTimeout( setRed, 1000)
    }

    function setGreen(){
        setColor('green')
    }

    function setPurple(){
        setColor('purple')
    }

    function setRed(){
        setColor('red')
    }



  let morseStringCharacters = []
    morseString.map(function (c) {

      for (let i = 0; i < c.length; i++) {
       console.log('char?', c[i])
       morseStringCharacters.push(c[i])
      }

  });

  function animationStart(){
morseStringCharacters.map((char)=>{

    if(char === '.'){
        setTheTimeOutGreen();

    }

    if(char === '_') {
        setTheTimeOutPurple()

    }
     else {

        setTheTimeOutRed()

    }

})
  }

  // if(c === ".") {setTimeout((color = "green"), 100)})

