  //     { name: "long", duration: 0.3, color: "green" },
  //   ];

  return (
    <div>
      <p>hi from MorseToAnimation: {morseString}</p>
      <div style={{ backgroundColor: `${color}` }}>Flashy</div>
    </div>
  );
}

