import React from "react";

export default function MorseToAnimation({ color,name }) {
  const lightBulb = {
    width: "200px",
    height: "200px",
    borderRadius: "50%",
    backgroundColor: color,
    fontSize: '40px',
    fontWeight: 'bolder',
  };

  return (
    <div>
      <div style={lightBulb}><span className="name">{name}</span></div>
    </div>
  );
}

